mapper.select:
SELECT ROUND(SUM(f.prg) / COUNT(f.feat_no), 2) AS progress, COUNT(f.feat_no) AS total, COUNT(CASE WHEN f.prg < 100 AND SYSDATE < f.pre_end_dt THEN 1 END) AS presentCount, COUNT(CASE WHEN f.prg = 100 THEN 1 END) AS complete, COUNT(CASE WHEN f.prg < 100 AND SYSDATE > f.pre_end_dt THEN 1 END) AS delay FROM System s JOIN Feature f ON f.sys_no = s.sys_no WHERE s.prj_no = #{prjNo}
AND (f.sys_no = #{systemNo} OR f.sys_no IN ( SELECT sys_no FROM System START WITH sys_no = #{systemNo} CONNECT BY PRIOR sys_no = par_sys_no ))
AND f.class_cd = #{featClassCd}

SELECT * FROM ( SELECT sub.*, ROW_NUMBER() OVER (ORDER BY sub.order_no) AS rn FROM ( SELECT f.feat_no AS featureNo, f.feat_id AS featureId, f.feat_title AS featureTitle, m.mem_nm AS memberName, f.prg AS progress, s.sys_ttl AS system, st.cd_dtl_nm AS status, st.order_no, CASE WHEN f.pre_end_dt >= SYSDATE THEN 'D-' || TRUNC(f.pre_end_dt - SYSDATE) ELSE 'D+' || ABS(TRUNC(SYSDATE - f.pre_end_dt)) END AS remainingDays FROM feature f LEFT JOIN system s ON f.sys_no = s.sys_no LEFT JOIN member m ON f.mem_no = m.mem_no JOIN codedetail st ON f.stat_cd = st.cd_dtl_no WHERE f.prj_no = #{prjNo}
AND (s.sys_no = #{systemNo} OR s.sys_no IN ( SELECT sys_no FROM system START WITH sys_no = #{systemNo} CONNECT BY PRIOR sys_no = par_sys_no))
AND f.class_cd = #{featClassCd}
AND f.stat_cd = #{cri.type}
AND LOWER(m.mem_nm) LIKE LOWER('%' || #{cri.keyword} || '%')
) sub ) WHERE rn BETWEEN #{cri.startRow} AND #{cri.endRow}

mapper.update:
UPDATE feature
SET
feat_id = #{featId},
feat_title = #{featTitle},
feat_cont = #{featDescription}

    , pre_st_dt = TO_DATE(#{preStartDateStr}, 'YYYY-MM-DD')


    , pre_end_dt = TO_DATE(#{preEndDateStr}, 'YYYY-MM-DD')


    , st_dt = TO_DATE(#{startDateStr}, 'YYYY-MM-DD')


    , end_dt = TO_DATE(#{endDateStr}, 'YYYY-MM-DD')


    , stat_cd = #{statusCode}


    , pri_cd = #{priorCode}


    , prg = #{progress}


    , diff_cd = #{diffCode}


    , class_cd = #{classCode}


    , sys_no = #{systemNo}


    , mem_no = #{memberNo}


    , tm_no = #{teamNo}

WHERE feat_no = #{featNo}

mapper.select:
SELECT COUNT(*)
FROM feature f
LEFT JOIN system s ON f.sys_no = s.sys_no
LEFT JOIN member m ON f.mem_no = m.mem_no
JOIN codedetail st ON f.stat_cd = st.cd_dtl_no
WHERE f.prj_no = #{prjNo}

    AND (f.sys_no = #{systemNo}
    OR f.sys_no IN (
    SELECT sys_no FROM system
    START WITH sys_no = #{systemNo}
    CONNECT BY PRIOR sys_no = par_sys_no
    ))


    AND f.class_cd = #{featClassCd}


    AND f.stat_cd = #{cri.type}


    AND LOWER(m.mem_nm) LIKE LOWER('%' || #{cri.keyword} || '%')
