mapper.select:
SELECT * FROM ( SELECT sub.*, ROWNUM rn FROM ( SELECT t.test_no AS testNumber, t.test_id AS testId, TRIM(cd_type.cd_dtl_nm) AS testType, t.test_title AS testTitle, sys_work.sys_ttl AS workTitle, TO_CHAR(t.test_st_dt, 'YYYY-MM-DD') AS testStartDate, TO_CHAR(t.test_end_dt, 'YYYY-MM-DD') AS testEndDate, COUNT(DISTINCT td.test_dtl_no) AS detailCount, COUNT(d.df_no) AS defectCount, TRIM(cd_stat.cd_dtl_nm) AS testStatus, count(*) over() totalElements FROM testmaster t LEFT JOIN testdetail td ON t.test_no = td.test_no LEFT JOIN codedetail cd_type ON t.type_cd = cd_type.cd_dtl_no LEFT JOIN codedetail cd_stat ON t.stat_cd = cd_stat.cd_dtl_no LEFT JOIN defect d ON td.test_dtl_no = d.test_dtl_no LEFT JOIN system sys_work ON sys_work.sys_no = t.sys_work_no WHERE t.prj_no = #{projectNumber} AND t.use_yn = 'Y'
AND t.sys_work_no = #{workNumber, jdbcType=INTEGER} OR sys_work.par_sys_no = #{workNumber, jdbcType=INTEGER}
AND t.type_cd = #{testType, jdbcType=CHAR}
AND (t.test_title LIKE '%' || #{search} || '%' OR t.test_id LIKE '%' || #{search} || '%')
AND t.stat_cd = #{status, jdbcType=CHAR}
GROUP BY t.test_no, t.test_id, cd_type.cd_dtl_nm, t.test_title, sys_work.sys_ttl, t.test_st_dt, t.test_end_dt, cd_stat.cd_dtl_nm, t.reg_dt ORDER BY t.reg_dt desc ) sub
WHERE ROWNUM <= #{page} * #{limit}
) WHERE rn > (#{page} - 1) * #{limit}

SELECT feat_no AS featureNo, feat_title AS featureName
FROM feature
WHERE prj_no = #{projectNumber}
