mapper.select:
SELECT t.*, CASE WHEN t.tsk_stat_cd = 'PMS00101' THEN '대기' WHEN t.tsk_stat_cd = 'PMS00102' THEN '진행중' ELSE '완료' END AS wbs_status, ( SELECT LISTAGG(m.mem_nm, ',') WITHIN GROUP (ORDER BY m.mem_no) FROM taskmember tm JOIN member m ON tm.mem_no = m.mem_no WHERE tm.prj_no = t.prj_no AND tm.tsk_no = t.tsk_no ) AS mem_nms, ( SELECT LISTAGG(tm.tm_no, ',') WITHIN GROUP (ORDER BY tm.tm_no) FROM taskmember tm WHERE tm.prj_no = t.prj_no AND tm.tsk_no = t.tsk_no ) AS tm_numbers, ( SELECT LISTAGG(tm.mem_no, ',') WITHIN GROUP (ORDER BY tm.mem_no) FROM taskmember tm WHERE tm.prj_no = t.prj_no AND tm.tsk_no = t.tsk_no ) AS mem_numbers FROM task t WHERE t.prj_no = #{prj_no}
AND t.tsk_no = #{tsk_no}
order by t.tsk_no

mapper.update:
UPDATE task
SET order_no = #{orderNo}

    , par_task_no = #{parentNo}

WHERE tsk_no = #{wbsNo}

UPDATE task
SET tsk_ttl = #{tsk_ttl}

    , tsk_stat_cd = #{tsk_stat_cd}


    , pre_st_dt = TO_DATE(#{pre_st_dt}, 'YYYY-MM-DD')


    , pre_end_dt = TO_DATE(#{pre_end_dt}, 'YYYY-MM-DD')


    , st_dt = TO_DATE(#{st_dt}, 'YYYY-MM-DD')


    , end_dt = TO_DATE(#{end_dt}, 'YYYY-MM-DD')


   , weight_val = #{weight_val}


   , prg = #{prg}


    , rel_out_nm = #{rel_out_nm}


    , sys_no = #{sys_no}

WHERE prj_no = #{prj_no}
AND tsk_no = #{tsk_no}

mapper.delete:
delete from taskmember
where prj_no = #{prj_no}
and tsk_no = #{tsk_no}

DELETE FROM task
WHERE tsk_no IN
(
    SELECT tsk_no
    FROM task
    WHERE prj_no = #{prj_no}
    START WITH tsk_no = #{tsk_no}
    CONNECT BY PRIOR tsk_no = par_task_no
)
