mapper.select:
SELECT * FROM ( SELECT sub.*, ROWNUM rn FROM ( SELECT defect.df_no AS defectNumber, defect.df_id AS defectId, defect.df_ttl AS defectTitle, pri.cd_dtl_nm AS priority, st.cd_dtl_nm AS status, mem_fd.mem_nm as discoverName, mem_work.mem_nm as workName, defect.df_fd_dt AS discoverDate, defect.due_dt AS scheduleWorkDate, defect.work_dt AS workDate FROM defect LEFT JOIN member mem_fd ON defect.mem_fd_no = mem_fd.mem_no LEFT JOIN member mem_work ON defect.mem_work_no = mem_work.mem_no LEFT JOIN codedetail pri ON defect.pri_cd = pri.cd_dtl_no LEFT JOIN System sys ON defect.work_no = sys.sys_no LEFT JOIN codedetail st ON defect.stat_cd = st.cd_dtl_no WHERE defect.prj_no = #{projectNumber}
AND defect.work_no = #{workNo} OR sys.par_sys_no = #{workNo}
AND type_cd = #{type}
AND stat_cd = #{status}
AND (df_id LIKE '%' || #{search} || '%' OR df_ttl LIKE '%' || #{search} || '%' OR df_cont LIKE '%' || #{search} || '%')
ORDER BY df_fd_dt DESC ) sub
WHERE ROWNUM <= #{page} * #{limit}
) WHERE rn > (#{page} - 1) * #{limit}

SELECT COUNT(*) FROM defect LEFT JOIN member mem_fd ON defect.mem_fd_no = mem_fd.mem_no LEFT JOIN member mem_work ON defect.mem_work_no = mem_work.mem_no LEFT JOIN codedetail pri ON defect.pri_cd = pri.cd_dtl_no LEFT JOIN codedetail st ON defect.stat_cd = st.cd_dtl_no WHERE prj_no = #{projectNumber}
AND work_no = #{workNo}
AND stat_cd = #{status}
AND (df_id LIKE '%' || #{search} || '%' OR df_ttl LIKE '%' || #{search} || '%' OR df_cont LIKE '%' || #{search} || '%')

mapper.update:
UPDATE defect


        fl_ms_fd_no = #{fileMasterFoundNumber, jdbcType=NUMERIC},


        fl_ms_work_no = #{fileMasterWorkNumber, jdbcType=NUMERIC}


WHERE df_no = #{no}
