mapper.update:
UPDATE risk
RSK_TTL = #{riskTitle}, CLASS_CD = #{classCode}, RISK_ID = #{riskId}, PRI_CD = #{priorCode}, RISK_CONT = #{riskContent}, RISK_PLAN = #{riskPlan}, DUE_DT = #{dueDate}, COMPL_DT = #{completeDate}, STAT_CD = #{statusCode}, SYS_NO = #{systemNo, jdbcType=NUMERIC}, MEM_NO = #{memberNo}, PRJ_NO = #{prjNo}
WHERE RISK_NO = #{riskNumber}

mapper.select:
SELECT * FROM ( SELECT r.risk_no AS riskNo, r.rsk_ttl AS riskTitle, m.mem_nm AS findMember, class.cd_dtl_nm AS className, stat.cd_dtl_nm AS status, pri.cd_dtl_nm AS priority, SUBSTR((SELECT SYS_CONNECT_BY_PATH(s2.sys_ttl, ' > ') FROM system s2 WHERE s2.sys_no = s.sys_no START WITH s2.par_sys_no IS NULL CONNECT BY PRIOR s2.sys_no = s2.par_sys_no), 3) AS system, r.due_dt AS dueDate, r.compl_dt AS completeDate, ROW_NUMBER() OVER (ORDER BY NVL(s.par_sys_no, s.sys_no), CASE WHEN s.par_sys_no IS NULL THEN 0 ELSE 1 END, s.sys_no) AS rn FROM risk r LEFT JOIN system s ON r.sys_no = s.sys_no LEFT JOIN member m ON r.mem_no = m.mem_no JOIN codedetail type ON r.type_cd = type.cd_dtl_no JOIN codedetail class ON r.class_cd = class.cd_dtl_no JOIN codedetail stat ON r.stat_cd = stat.cd_dtl_no JOIN codedetail pri ON r.pri_cd = pri.cd_dtl_no WHERE r.prj_no = #{prjNo, jdbcType=NUMERIC}
AND (s.sys_no = #{systemNo, jdbcType=NUMERIC} OR s.sys_no IN ( SELECT sys_no FROM system START WITH sys_no = #{systemNo, jdbcType=NUMERIC} CONNECT BY PRIOR sys_no = par_sys_no))
AND r.class_cd = #{selectedClassNo}
AND r.stat_cd = #{selectedStatusNo}
AND r.pri_cd = #{selectedPriorNo}
AND LOWER(r.rsk_ttl) LIKE LOWER('%' || #{keyword} || '%')
) WHERE rn BETWEEN #{startRow, jdbcType=NUMERIC} AND #{endRow, jdbcType=NUMERIC}

SELECT COUNT(*) AS totalRecords
FROM
risk r
LEFT JOIN system s ON r.sys_no = s.sys_no
LEFT JOIN member m ON r.mem_no = m.mem_no
JOIN codedetail type ON r.type_cd = type.cd_dtl_no
JOIN codedetail class ON r.class_cd = class.cd_dtl_no
JOIN codedetail stat ON r.stat_cd = stat.cd_dtl_no
JOIN codedetail pri ON r.pri_cd = pri.cd_dtl_no
WHERE
r.prj_no = #{prjNo, jdbcType=NUMERIC}


    AND (s.sys_no = #{systemNo, jdbcType=NUMERIC}
    OR s.sys_no IN (
    SELECT sys_no FROM system
    START WITH sys_no = #{systemNo, jdbcType=NUMERIC}
    CONNECT BY PRIOR sys_no = par_sys_no))



    AND r.class_cd = #{selectedClassNo}



    AND r.stat_cd = #{selectedStatusNo}



    AND r.pri_cd = #{selectedPriorNo}



    AND LOWER(r.rsk_ttl) LIKE LOWER('%' || #{keyword} || '%')
